import React from "react";

function Modal({ isOpen, onClose }) {
  if (!isOpen) return null;

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: "rgba(0, 0, 0, 0.5)",
        zIndex: 999,
      }}
    >
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          backgroundColor: "white",
          padding: 20,
          width: "100%", // Imposta la larghezza desiderata
          height: "10%", // Imposta l'altezza desiderata
          maxWidth: 400, // Specifica una larghezza massima
          maxHeight: 300, // Specifica un'altezza massima
        }}
      >
        <h2>Questo Ã¨ un modale</h2>
        <p>Contenuto del modale...</p>
        <button onClick={onClose}>Chiudi Modale</button>
      </div>
    </div>
  );
}

export default Modal;
